import { map } from 'rxjs/operators';
import * as i0 from "@angular/core";
import * as i1 from "src/app/services/userEvent.service";
import * as i2 from "@angular/router";
import * as i3 from "src/app/services/resume.service";
export class CvFullComponent {
    constructor(userEventService, route, router, resumeService) {
        this.userEventService = userEventService;
        this.route = route;
        this.router = router;
        this.resumeService = resumeService;
        this.id = 0;
        this.toDataURL = async (url) => {
            console.log("Downloading image...");
            var res = await fetch(url);
            var blob = await res.blob();
            const result = await new Promise((resolve, reject) => {
                var reader = new FileReader();
                reader.addEventListener("load", function () {
                    resolve(reader.result);
                }, false);
                reader.onerror = () => {
                    return reject(this);
                };
                reader.readAsDataURL(blob);
            });
            return result;
        };
    }
    ngOnInit() {
        this.route.params.pipe(map(params => params['id'])).subscribe(id => {
            this.resumeService.getResumeById(12).subscribe({
                next: resume => {
                    console.log('id', id);
                    console.log('resume', resume);
                    this.cv = resume;
                },
                error: error => console.log(error)
            });
        });
    }
}
